import {createGlobalStyle} from 'styled-components';

export const GlobalStyles = createGlobalStyle`
body
{
    font-family: "Open sans Condensed";
    padding: 20px 60px;
}

a {
    text-decoration: none;
    color: black;
}

*{
    box-sizing: border-box;
}
`;
