import React from 'react';
import './shopping-cart-drop-down.styles.scss';
import CustomButton from '../customButton/customButton.component';
import {connect} from "react-redux";
import CartItem from '../card-item/cart-item.component';
import { selectCartItems } from '../../redux/cart/cart.selectors'
import {createStructuredSelector} from 'reselect'
import {withRouter } from 'react-router-dom'
import {toggleCartHidden} from '../../redux/cart/cart.action';

const ShoppingCartDropDown = ({cartItems, history, dispatch})=>(
    <div className="cart-dropdown">
        <div className="cart-items" >
            {
                cartItems.length ? 
                (cartItems.map((cartItem)=>
                (<CartItem key={cartItem.id} item={cartItem} />)))
                : (<span className="empty-message">Your cart is empty</span>)
            }
        </div>
        <CustomButton onClick={()=>{history.push('/checkout'); dispatch(toggleCartHidden()) } } >GO TO CHECKOUT</CustomButton>
    </div>
)

const mapStateToProps = createStructuredSelector({
    cartItems: selectCartItems
})

export default withRouter(connect(mapStateToProps)(ShoppingCartDropDown));
